mapper.update:
update data_hj set  date_updated = strftime('%s000','now')
     ,x1 = #{x1}
     ,x2 = #{x2}
     ,x3 = #{x3}
     ,x4 = #{x4}
     ,x5 = #{x5}
     ,x6 = #{x6}
     ,x7 = #{x7}
     ,x8 = #{x8}
     ,x9 = #{x9}
     ,x10 = #{x10}
     ,x11 = #{x11}
where
data_analyze_id = #{dataAnalyzeId}

mapper.select:
select

from data_hj t1
inner join data_analyze t2 on t2.id=t1.data_analyze_id and t2.data_format='HJ'
inner join data_device t3 on t3.id=t2.data_device_id
where 1=1
  and t3.date_detection = #{dateDetection}
 and t3.test_technology = #{testTechnology}
 and t3.electric_bureau = #{electricBureau}
     and t3.substation = #{substation}
   and t3.voltage_level = #{voltageLevel}
     and t3.device_type = #{deviceType}


        and t3.device_name in ('#',#{deviceName})


        and t3.device_name = '#'


      and t3.space_name = #{spaceName}
     and t3.folder_path = #{folderPath}
limit 1

select

from data_hj t1
inner join data_analyze t2 on t2.id=t1.data_analyze_id and t2.data_format='HJ'
inner join data_device t3 on t3.id=t2.data_device_id
where 1=1
  and t3.date_detection = #{dateDetection}
 and t3.test_technology = #{testTechnology}
 and t3.electric_bureau = #{electricBureau}
     and t3.substation = #{substation}
   and t3.voltage_level = #{voltageLevel}
     and t3.device_type = #{deviceType}
      and t3.space_name = #{spaceName}
     and t3.folder_path = #{folderPath}
